import React,{useState} from 'react'
import '../App.css'

const Person = (props) => {
    const {name,height,gender,money,age,index,people,setPeople} = props
    const [likes,setLikes] = useState(0)

//Likes------------------------
    const handleLike=() =>{
        setLikes(likes + 1)
    }

    const handleDislike=() =>{
        if(likes > 0)
        setLikes(likes - 1)
    }

//Money-------------------------

const updateMoney=(index, isMore) =>{
    //here we create a new array called newPeople
    //using a copy of people via the SPREAD operator
    const newPeople = [...people]
    if(isMore){
        (newPeople[index].money++)
    }else{
        (newPeople[index].money--)
    }
    setPeople(newPeople)
}

return (
    <div className='personCard'>
    <h2>{name}</h2>
    <p>Gender: {gender}</p>
    <p>Age: {age}</p>
    <p>Money: {money}</p>
    <p>Height: {height}</p>
    <p>Likes: {likes}</p>
    <button id="like" onClick={handleLike}>Like {name}'s Profile</button>
    <button id="dislike" onClick={handleDislike}>Dislike {name}'s Profile</button>
    <button id="give" onClick={updateMoney(index,true)}>Give</button>
    <button id="take" onClick={updateMoney(index,true)}>Take</button>

    </div>
)
}

export default Person
